DROP TABLE IF EXISTS publictoto cascade;
CREATE TABLE publictoto(
  publictoto_id        SERIAL PRIMARY KEY
  ,Name                VARCHAR not null
  ,Location            VARCHAR 
  ,Latitude            NUMERIC not null
  ,Longitude           NUMERIC not null
  ,Open_YearRound      VARCHAR
  ,Handicap_Accessible VARCHAR
  ,Borough             VARCHAR
  ,Comments            VARCHAR
);
